var Is_Scroll=!1,diff=0,Is_device_motion_change=null,Count_device_motion=0,device_motion_compare=0;let frames=[];function appendIframe(){document.querySelectorAll(".skycom-wrapper").forEach((function(e){if(!e)return;const n=document.createElement("iframe");n.src="https://testform.skycom.vn/test",n.classList.add("skycom-iframe"),e.insertBefore(n,e.children[0])}))}function checkDeviceEmotion(){window.DeviceMotionEvent&&(window.addEventListener("devicemotion",(function(e){diff=e.acceleration.x||e.accelerationIncludingGravity.x,handleDeviceMotionStatus(),frames.forEach((e=>{sendMessage(e)}))})),setInterval((()=>{diff!=device_motion_compare&&(Count_device_motion++,device_motion_compare=diff)}),1e3))}function handleDeviceMotionStatus(){Is_device_motion_change=Count_device_motion>5}function checkScroll(){window.addEventListener("scroll",(function(e){Is_Scroll||(Is_Scroll=!0,frames.forEach((e=>{sendMessage(e)})))}))}function listenFirame(){document.querySelectorAll(".skycom-iframe").forEach((function(e){frames.push(e),e.addEventListener("load",(function(){sendMessage(e)}))}))}function sendMessage(e){const n=JSON.stringify({message:"send data",src:window.location.href,Is_Scroll:Is_Scroll,Is_device_motion_change:Is_device_motion_change,Count_device_motion:Count_device_motion});e.contentWindow.postMessage({call:"skylink_event",value:n},"https://testform.skycom.vn/")}appendIframe(),listenFirame(),checkDeviceEmotion(),checkScroll();